import { useState } from 'react';
import styles from './Login.module.css'; // Seu CSS importado normalmente

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  return (
    <div className={styles.container}>
      <h2>Logue para ter Acesso ao Site</h2>
      <form>
        <label>
          <span>E-mail:</span>
          <input
            type="email"
            name="email"
            required
            placeholder="E-mail"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </label>

        <label>
          <span>Senha:</span>
          <input
            type="password"
            name="password"
            required
            placeholder="Senha"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </label>

        <button type="submit" className={styles.btnCadastrar}>
          Logar
        </button>
      </form>
    </div>
  );
};

export default Login;
