import logo from './img/logo.svg';
import './App.css';
import React, { useState } from "react";
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Home from './screens/Home';
import Login from './screens/Login';
import Contact from './screens/Contact';
import About from './screens/About';
import Cadastrar from './screens/Cadastrar';
import NavBar from './components/navbar';//ifydtfuygujipkjhgyfdtryughjkl,
//importante (add pelo prof, serve pra afirma as rotas)

function App() {
  const [formData, setFormData] = useState({
          name: "",
          email: "",
          password: ""
      });
  
      const handleChange = (e) => {
          setFormData({
              ...formData,
              [e.target.name]: e.target.value
          });
      };
  
      const handleSubmit = async (e) => {
          e.preventDefault();
          try {
              const response = await fetch("http://localhost:3001/cadastrar", {
                  method: "POST",
                  headers: {
                      "Content-Type": "application/json"
                  },
                  body: JSON.stringify(formData)
              });
              const result = await response.json();
              alert(result.message);
          } catch (error) {
              console.error("Erro ao cadastrar:", error);
          }
      };

  return (
    <div className="App">
      <header className="App-header">
      <img src={logo} className="App-logo" alt="logo" />
      <BrowserRouter>
      <NavBar/>
        <div className="container">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<Login />} />
            <Route path="/contact" element={<Contact />} />
            <Route path="/about" element={<About />} />
            <Route path="/cadastrar" element={<Cadastrar />} />
          </Routes>
        </div>
      </BrowserRouter>
      </header>
      <div className="App-fundo" alt="fundo" ></div>
      <div className={styles.container}>
            <h2>Cadastro de Usuário</h2>
            <form onSubmit={handleSubmit} className={form}>
                <label>Nome:</label>
                <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    required
                />

                <label>Email:</label>
                <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                />

                <label>Senha:</label>
                <input
                    type="password"
                    name="password"
                    value={formData.password}
                    onChange={handleChange}
                    required
                />

                <button type="submit">Cadastrar</button>
            </form>
        </div>

    </div>
  );
}

export default App;
