import { useState, useEffect } from 'react';
import styles from './Loja.module.css';
import { useAuthentication } from '../hooks/useAuthentication';
 
const RegisterStore = () => {
  useEffect(() => {
    document.title = " Cadastrar Loja"
  }, [])
 
const [displayName, setName]=useState('');
const [displayEndereco, setEndereco]  = useState('');
const [displayCidade, setCidade]  = useState('');
const [displayUF, setUf]  = useState('');
const [error, setError] = useState('');
const {createUser , error: authError, loading} = useAuthentication();
 
const handleSubmit = async (e) => {
    e.preventDefault();
    setError("")
 
    const user = {
        displayName,
        displayEndereco,
        displayCidade,
        displayUF
    }
    const res = await createUser(user);
}
  useEffect(() =>{
    if(authError){
      setError(authError);
    }
  }, [authError]);
 
 
  return (
        <div className={styles.container}>
            <h2>Cadastre sua Loja</h2>
            <div className={styles.formContainer}>
                <form onSubmit={handleSubmit}>
                    <div className={styles.formGroup}>
                        <span>Nome:</span>
                        <input 
                            type="text" 
                            name="displayName" 
                            required 
                            placeholder="Nome do Usuário" 
                            value={displayName} 
                            onChange={(e) => setName(e.target.value)}
                        />
                    </div>
                    <div className={styles.formGroup}>
                        <span>Endereço:</span>
                        <input 
                            type="text" 
                            name="displayEndereco" 
                            required 
                            placeholder="Endereço" 
                            value={displayEndereco} 
                            onChange={(e) => setEndereco(e.target.value)}
                        />
                    </div>
                    <div className={styles.formGroup}>
                        <span>Cidade:</span>
                        <input 
                            type="text" 
                            name="displayCidade" 
                            required 
                            placeholder="Cidade" 
                            value={displayCidade} 
                            onChange={(e) => setCidade(e.target.value)}
                        />
                    </div>
                    <div className={styles.formGroup}>
                        <span>UF:</span>
                        <input 
                            type="text" 
                            name="displayUF" 
                            required 
                            placeholder="UF" 
                            value={displayUF} 
                            onChange={(e) => setUf(e.target.value)}
                        />
                    </div>
                    {!loading && <button className={styles.btn}>CADASTRAR</button>}
                    {loading && <button className={styles.btn} disabled>AGUARDE...</button>}
                    {error && <p className={styles.error}>{error}</p>}
                </form>
            </div>
        </div>
    )
}
 
export default RegisterStore
