import { getAuth } from "firebase/auth"; // Importando getAuth para autenticação

// Your web app's Firebase configuration
const firebaseConfig = {
  apiKey: "AIzaSyAaOt-b_ksxS3mFWabFqKBz9DWpkX9IOow",
  authDomain: "projeto-boer-535aa.firebaseapp.com",
  projectId: "projeto-boer-535aa",
  storageBucket: "projeto-boer-535aa.firebasestorage.app",
  messagingSenderId: "981638237183",
  appId: "1:981638237183:web:8735c7565311993eb635ca"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);

// Inicializando e exportando o serviço de autenticação
export const auth = getAuth(app);

